INSERT INTO `tp_article` VALUES (115,258,'254,258,',NULL,'1993','<p>代理台湾U-Reach拷贝机 设立无线通讯产品事业部</p>',NULL,'740999115','jsydl',0,1,'','成立五股烧录厂，致力于专业IC烧录',0,0),(126,257,'254,257,',NULL,'2003','',NULL,'1046144747','admin',0,1,'','成立华南(东莞）办事处',1,0),(134,260,'242,260,',NULL,'打造国家电子装备制造“无人工厂时代”','<p>&nbsp; &nbsp;<span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">2018年政府工作报告指出：“发展智能产业，拓展智能生活。运用新技术、新业态、新模式，大力改造提升传统产业。”智能制造等新兴产业迎来了新的历史发展机遇。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 春风吹，百草生。近年来，中国电子科技集团有限公司将智能制造作为供给侧结构性改革、打造新动能的一个重要领域，集聚集团公司各级成员单位的制造资源和创新能力，促进制造技术与数字化技术、互联化技术、智能化技术深度融合，牵头制定相关智能制造国家标准，推出智能制造整体解决方案，打造安全可控的智慧院所标杆和多个行业引领的智能制造示范工程，走出了一条独具特色的以装备为基础的智能制造之路。在过去单机设备研制的基础上，中国电科正逐步实现整线装备自动化、数字化、智能化，开启中国制造“无人工厂时代”。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 据悉，截至2017年9月，在国家级智能制造重大工程中，中国电科共有12个项目入选工信部和财政部联合实施智能制造专项行动项目，名列央企前茅。其中，目前已经建成国内首个500MW的高效光伏电池智能制造示范线、首个微电子共烧陶瓷器件数字化车间，作为工信部智能制造试点示范，成功经验已经在行业内复制推广。　　500MW的高效光伏电池智能制造示范线是国内智能装备国产化程度最高的太阳能电池制造工厂，生产中每一个环节都由计算机数字化把控，产品的良品率高达98%以上，机械臂和AGV小车每天承担35万片高效电池片精准无误的抓取和传递运输工作，厂房里的操作工人数量屈指可数。作为工信部首批智能制造试点示范，这条线在实现工艺设计数字化、制造装备自动化、生产管理信息化的基础上，可以在减少一半以上工作人员的同时，仍然将生产效率提高30%。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 微电子陶瓷器件数字化车间，则是针对多品种、小批量、订单式生产的微电子陶瓷器件智能制造和数字化车间建设需求，开展的数字化车间总体架构设计、智能生产线装备系统集成、制造执行系统集成应用等技术研究。该项目立足形成自主安全可控的智能制造装备、制造执行系统(MES)、数字化车间监控软件等产品和离散型数字化车间系统集成新模式服务能力，通过自主研制推动电子信息制造业技术进步。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 车间包括智能工艺设备、智能物流传输系统、工业以太网络、MES生产执行系统、数据采集与监视控制系统以及相应的硬件设施，可实现LTCC基板工艺设计数字化、制造装备自动化、生产管理信息化，使整个生产线上的人员可减少50%以上，LTCC基板的生产效率提高20%。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 两个典型应用，不过只是中国电科智能制造的一个缩影。“集团公司必须紧紧抓住这一重大历史机遇，重点推进智能制造和机器人研究。”2018年工作会上，总经理刘烈宏将“智能制造工程”列为2018年重点推进实施的“五项工程”之一，并强调要加强智能制造工程的顶层策划、大力发展集成电路制造关键装备设备、积极推进从传感器到整机的全产业链关键技术布局、推进一批智能制造示范工程等——对中国电科智能制造工程做出了全面而深入的部署。　　</span></p><p><span style=\"color: rgb(77, 79, 83); font-family: \" microsoft=\"\" stheiti=\"\" font-size:=\"\" letter-spacing:=\"\" background-color:=\"\">&nbsp; &nbsp; 智能制造，中国电科正在全力加速。</span></p>','/2018-03-23/5ab45debe4a4b.jpg','1521769803','admin',35,1,'','2018年政府工作报告指出：“发展智能产业，拓展智能生活。运用新技术、新业态、新模式，大力改造提升传统产业。”智能制造等新兴产业迎来了新的历史发展机遇。　　',1,0),(120,256,'254,256,',NULL,'不要','<p>忙来考虑</p>','/2018-02-25/5a921b041a616.jpg','1519524625','admin',0,1,'','',0,0),(121,257,'254,257,',NULL,'2002','<p>266</p>',NULL,'1014603091','admin',0,1,'','成立中国苏州总部',1,0),(122,256,'254,256,',NULL,'2008','',NULL,'1203907625','admin',0,1,'','广州捷普驻厂、启基(昆山）驻厂',1,0),(123,256,'254,256,',NULL,'2010','',NULL,'1270608551','admin',0,1,'','成立东莞嘉多利、代理ELNEC烧录器华硕集团驻厂',1,0),(124,256,'254,256,',NULL,'2011','',NULL,'1306637508','admin',0,1,'','广达驻厂、深圳中兴移动驻厂、代理台湾Dediprog烧录器',1,0),(125,256,'254,256,',NULL,'2013','',NULL,'1361761309','admin',0,1,'','代理台湾U-Reach拷贝机、设立无线通讯产品事业部',1,0),(127,257,'254,257,',NULL,'2005','',NULL,'1109303198','admin',0,1,'','深圳中兴通讯驻厂',1,0),(128,257,'254,257,',NULL,'2006','',NULL,'1140833491','admin',0,1,'','成立苏州嘉多利',1,0),(129,258,'254,258,',NULL,'1995','',NULL,'793685579','admin',0,1,'','收购英特利烧录厂',1,0),(130,258,'254,258,',NULL,'1996','',NULL,'835693515','admin',0,1,'','代理日本Minato烧录器',1,0),(131,258,'254,258,',NULL,'1997','',NULL,'867229515','admin',0,1,'','代理日本FSG烧录器',1,0),(132,258,'254,258,',NULL,'2001','',NULL,'983067091','admin',0,1,'','英华达（上海）驻厂',1,0),(133,260,'242,260,',NULL,'物联网、智能家居、人工智能','